import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import useEmployee from "../../Hooks/useEmployee";
import Navbar from "../Navbar/Navbar";

const EmployeeDetail = () => {
  const { id } = useParams();

  const [employeeDetails, setEmployeeDetails] = useState(null);
  const {updateEmployee} = useEmployee(); 

  const [newName, setnewName] = useState("");
  const [newAge, setNewAge] = useState("");
  const [newCountry, setNewCountry] = useState("");
  const [newPosition, setNewPosition] = useState("");
  const [newSalary, setNewSalary] = useState("");

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/employee/employees/${id}`)
      .then((response) => {
        // console.log(response.data)
        setEmployeeDetails(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  return (
    <div>
      <Navbar />
      {employeeDetails?.map((items) => (
        <div className="form-container" key={items.id}>
          <form>
            <label>FirstName: </label>
            <input
              type="text"
              placeholder="First Name..."
              required
              value={items.name}
              onChange={(e) => {
                setnewName(e.target.value);
              }}
            ></input>

            <label>Age: </label>
            <input
              type="number"
              placeholder="Age..."
              required
              value={items.age}
              onChange={(e) => {
                setNewAge(e.target.value);
              }}
            ></input>

            <label>Country: </label>
            <input
              type="text"
              placeholder="Country..."
              required
              value={items.country}
              onChange={(e) => {
                setNewCountry(e.target.value);
              }}
            ></input>

            <label>Position: </label>
            <input
              type="text"
              placeholder="Position..."
              required
              value={items.position}
              onChange={(e) => {
                setNewPosition(e.target.value);
              }}
            ></input>

            <label>Salary (Yearly): </label>
            <input
              type="number"
              placeholder="Wages..."
              required
              value={items.salary}
              onChange={(e) => {
                setNewSalary(e.target.value);
              }}
            ></input>

            <button type="submit" className="button" onClick={(updateStaff)}>
              Submit
            </button>
          </form>
        </div>
      ))}
    </div>
  );
};

export default EmployeeDetail;
