import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import useEmployee from "../../Hooks/useEmployee";

const EmployeeDetail = () => {
  const { id } = useParams();
  const { getEmployeeById, specificEmployee } = useEmployee();

  useEffect(() => {
    getEmployeeById(id);
  });
  return (
    <div>
      {" "}
      Display detail of employee clicked on: {id}
      {specificEmployee?.map((item) => {
        <h4>Employee Name: {item.name} </h4>;
      })}
    </div>
  );
};

export default EmployeeDetail;
