import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import useEmployee from '../../Hooks/useEmployee';

const EmployeeDetail = () => { 
    const { id } = useParams(); 
    const[specifiEmployee, setSpecificEmployee] = useState()
    
    const getEmployeeById = id =>{
        axios.get(`http://localhost:5000/api/v1/employee/employees/${id}`).then( (response) => { 
            console.log(response.data); 
        }).catch( (error) => { 
            console.log(error); 
        })
    }
   
   useEffect( () => { 
       getEmployeeById(id); 
   })
    return(
        <div> Display detail of employee clicked on: {id}
      
        </div>
    ); 
}

export default EmployeeDetail;
