    name,
    age,
    country,
    position,
    salary
  ) => {
    console.log("Hello I am coming from the hook");
    console.log(name + age + country + position + salary);
    await axios
      .post(`${BASE_URL}`, {
        name: name,
        age: age,
        country: country,
        position: position,
        salary: salary,
      })
      .then((response) => {
        console.log(response);
        alert("Inserted Successfully");
      })
      .catch((error) => {
        alert("Data could not be added");
      });
  };

  useEffect(() => {
    axios
      .get(`${BASE_URL}/employees`)
      .then((response) => {
        setEmployeeList(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  const getEmployeeById = (url) => {
    axios
      .get(url)
      .then((response) => {})
      .catch((error) => {
        console.log(error);
      });
  };

  return {
    submitEmployeeDetails,
    employeeList,
  };
};

export default useEmployee;
