import React from "react";
import axios from "axios"; 

const BASE_URL = "http://localhost:5000/api/v1/employeee/"; 

const useEmployee = () => {
  const submitEmployeeDetails = async (name, age, country, position, salary) => {
    console.log("Hello I am coming from the hook");
    console.log(name + age + country + position + salary);
    await axios.post(`${BASE_URL}`, {
        name: name, 
        age: age, 
        country: country, 
        position: position, 
        salary: salary
    }).then((response) => {  
        console.log(response); 
        alert("Inserted Successfully"); 
    }).catch ((error ) => { 
        alert("Data could not be added")
    })
  };

  const getEmployeeDetails = () => { 
      axios.get(`${BASE_URL}/employee`).then( (response) => { 
        console.log()
      }).catch( (error ) => { 
          console.log(error)
      })
  }

  return {
    submitEmployeeDetails,
  };
};

export default useEmployee;
