import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import useEmployee from "../../Hooks/useEmployee";
import Navbar from "../Navbar/Navbar";

const EmployeeDetail = () => {
  const { id } = useParams();

  const { updateEmployee } = useEmployee();

  const [newUser, setNewUser] = useState({
    newName: "", 
    newAge: "", 
    newCountry: "", 
    newPosition: "", 
    newSalary: ""
  }); 

  const {newName, newAge, newCountry, newPosition, newSalary} = newUser; 
  const onInputChange = e => { 
    setNewUser({...newUser, [e.target.value]: e.target.value}); 
  }
 
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/employee/employees/${id}`)
      .then((response) => {
        // console.log(response.data)
        setNewUser(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  });

  return (
    <div>
      <Navbar />

      <div className="form-container" >
        <form>
          <label>FirstName: </label>
          <input
            type="text"
            placeholder="First Name..."
            required
            value={newName}
            onChange={(e) => {
              onInputChange(e);
            }}
          ></input>

          <label>Age: </label>
          <input
            type="number"
            placeholder="Age..."
            required
            value={newAge}
            onChange={(e) => {
              onInputChange(e);
            }}
          ></input>

          <label>Country: </label>
          <input
            type="text"
            placeholder="Country..."
            required
            value={newCountry}
            onChange={(e) => {
              onInputChange(e);
            }}
          ></input>

          <label>Position: </label>
          <input
            type="text"
            placeholder="Position..."
            required
            value={newPosition}
            onChange={(e) => {
              onInputChange(e);
            }}
          ></input>

          <label>Salary (Yearly): </label>
          <input
            type="number"
            placeholder="Wages..."
            required
            value={newSalary}
            onChange={(e) => {
              onInputChange(e);
            }}
          ></input>

          <button type="submit" className="button" onClick={() => {}}>
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default EmployeeDetail;
