import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Navbar/Navbar";

const EmployeeDetail = () => {
  const { id } = useParams();
  
  const [employeeDetails, setEmployeeDetails] = useState('');

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/employee/employees/${id}`)
      .then((response) => {
        // console.log(response.data)
        setEmployeeDetails(response.data);
     
      })
      .catch((error) => {
        console.log(error);
      });
  });

  console.log(employeeDetails.name)

  return (
    <div>
      <Navbar />
    {employeeDetails.length===0? (
      <div className="form-container">
        <form>
          <label>FirstName: </label>
          <input type="text" placeholder="First Name..." required value={employeeDetails.name}></input>

          <label>Age: </label>
          <input type="number" placeholder="Age..." required></input>

          <label>Country: </label>
          <input type="text" placeholder="Country..." required></input>

          <label>Position: </label>
          <input type="text" placeholder="Position..." required></input>

          <label>Salary (Yearly): </label>
          <input type="number" placeholder="Wages..." required></input>

          <button type="submit" className="button">
            Submit
          </button>
        </form>
      </div>
    ):(
      <div>
        <p>Please select an employee...</p>
      </div>
    )}
      
    </div>
  );
};

export default EmployeeDetail;
